mapper.select:
SELECT id, device_name, reg_date, is_on, device_id, row_number() over(ORDER BY reg_date) AS row_num FROM (
SELECT A.id, B.name AS device_name, A.reg_date, A.is_on, B.id as device_id FROM viewer_onoff_history A JOIN viewer_info B ON A.viewer_id = B.id Union SELECT A.id, B.name AS device_name, A.reg_date, A.is_on, B.id as device_id FROM xray_onoff_history A JOIN xray_info B ON A.xray_id = B.id
SELECT A.id, B.name AS device_name, A.reg_date, A.is_on, B.id as device_id FROM viewer_onoff_history A JOIN viewer_info B ON A.viewer_id = B.id
SELECT A.id, B.name AS device_name, A.reg_date, A.is_on, B.id as device_id FROM xray_onoff_history A JOIN xray_info B ON A.xray_id = B.id
) as subquery
AND device_id = #{deviceId}
ORDER BY row_num DESC;
